import React from 'react';

const Blog = () => {
  const componentStyle = {
   
    borderRadius: '16px',
    marginTop: '50px',
    border: '2px solid #ccc', // Optional: Add a border for better visibility
    padding: '20px', // Optional: Add padding for content
    boxSizing: 'border-box', // Ensure that padding and border are included in the total width and height
  };
 
  return (
    <div style={componentStyle} >
        
      {/* Your component content goes here */}
      <div className='img'>
      <img className='m-2 p-10'
        src="https://www.ahst.co.uk/wp-content/uploads/2016/07/600x400.png" 
        alt="Blog Post Image"
      />
      </div>
      <p>Blog Post Title</p>
      <p>This is a short excerpt from the blog post content to give readers a teaser...</p>
      <label>Read More</label>
    </div>
    
  );
};

export default Blog;
