import React, { useState } from 'react';

const Register = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Data Submitted:', formData);
    // Add logic to handle form submission (e.g., API call, state update, etc.)
  };

  return (
    <form class="dropdown-menu p-4">
    <div class="form-group">
      <input type="email" class="form-control" id="exampleDropdownFormEmail2" placeholder="Blog title"/>
    </div>
    <div class="form-group">
      <input type="text" class="form-control" id="exampleDropdownFormPassword2" placeholder="Content"/>
    </div>
    <div class="form-group">
    <input type="text" class="form-control" id="exampleDropdownFormPassword2" placeholder="Content"/>
    </div>
    <button type="submit" class="btn btn-primary">Publish</button>
  </form>
  );
};

export default Register;
