import {React, useState} from 'react'

const Register = () => {
    const [name, setName] = useState('');
    const [age, setAge] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confPassword, setConfPassword] = useState('');
  
  
    const handleChange = (e) => {
      setName(e.target.value);
    }
    const handleEmailChange = (e) => {
      setEmail(e.target.value);
    }
  
    
    const handleSubmit = (e) => {
      if (password !== confPassword) {
        console.log("password Not Match");
      } else {
        console.log('A form was submitted with Name :"' + name +
        '" ,Age :"' + age + '" and Email :"' + email + '"');
      }
      e.preventDefault();
    }
  
  return (
    <div className="App">
      <header className="App-header">
        <form onSubmit={(e) => { handleSubmit(e) }}>
          <h2> Create New Blog</h2>
        
          <input type="text" placeholder='Blog-title' value={name} required onChange={(e)=> { handleChange(e) }} /><br />
         
          <input type="text" placeholder='Content' value={email} required onChange={(e) => { handleEmailChange(e) }} /><br />
          
          <input type="text" value={name} required onChange={(e)=> { handleChange(e) }} /><br />
          
          <input type="submit" value="Publish" />
        </form>
      </header>
    </div>
  );
}
export default Register
