import {React, useEffect, useState} from 'react'

const Register = () => {
    const [name, setName] = useState('');
    const [count, setCount] = useState(0);
    const clearUp = () => {
    const [widthCount,setWidthCount] = useState(window.screen.width);
    const currentScrren = () =>{
      setWidthCount(() => window.innerwidth)
    };
    useEffect( () => {
      window.addEventListener("resize", currentScrren)
    },[widthCount])
   const handleSubmit = (e) => {
        console.log('A form was submitted with Name :"' + name );
      e.preventDefault();
    }
   
 
    // useEffect( () => {
    //   document.title = count;
    // })
  
  return (
    <div className="App">
      <header className="App-header">
        <form onSubmit={(e) => { handleSubmit(e) }}>
          {/* <h2> Create New Blog</h2> */}
          <button onClick={() => setCount(count + 1)}> + </button>
          <h1>{count}</h1>
          <button onClick={() => (count===0 ? setCount(0) :  setCount(count - 1))}> - </button>
       
          {/* <input type="text" placeholder='Blog-title' value={name} required onChange={(e)=> { handleChange(e) }} /><br />
         
          <input type="text" placeholder='Content' value={text} required onChange={(e) => { handleChange(e) }} /><br />
          
          <input type="text" value={name} required onChange={(e)=> { handleChange(e) }} /><br />
          <div className='btn'>
          <input type="submit" value="Publish" />
          </div> */}
        </form>
        <span>the size of window screen is {widthCount}</span>
      </header>
    </div>
  );
}
}
clearUp;

export default Register
